window.addEventListener('resize', function () {
	if (window.innerWidth <= 992) {
		let body = document.querySelectorAll('.item-slider__body')
		for (let index = 0; index < body.length; index++) {
			const element = body[index];
			element.style.height = '0px'
			element.style.color = '#333647'

		}
	} else {
		let body = document.querySelectorAll('.item-slider__body')
		for (let index = 0; index < body.length; index++) {
			const element = body[index];
			element.style.height = 'auto'
			element.style.color = '#6D6F81'

		}
	}
})


if (window.innerWidth <= 992) {
	console.log('aa')
	let title = document.querySelectorAll('.item-slider__title')
	console.log(title)
	let height = []
	let body = document.querySelectorAll('.item-slider__body')
	for (let index = 0; index < title.length; index++) {
		const element = title[index];
		height.push(body[index].clientHeight)
		body[index].style.height = '0px'
		// console.log(element)
		element.addEventListener('click', function () {
			if (window.innerWidth <= 992) {
				body[index].classList.toggle('_active')
				if (body[index].classList.contains('_active')) {
					body[index].style.height = `${height[index]}px`;
					element.style.color = '#6D6F81'
				} else {
					body[index].style.height = '0px';
					element.style.color = '#333647'
				}
			}
		})

	}

}

